class Scene2 extends Phaser.Scene{constructor(){super("PlayGame")}preload(){this.load.image("Fuel","assets/images/Fuel.png"),this.load.image("Leftlane","assets/images/Platform.png"),this.load.image("Rightlane","assets/images/Platform.png"),this.load.image("Roadblock","assets/images/RoadBlock.png"),this.load.image("Roadlines","assets/images/Roadlines.png"),this.load.image("Platform","assets/images/Platform.png"),this.load.image("biker","assets/images/Biker.png")}create(){this.background=this.add.image(0,0,"background"),this.background.setOrigin(0,0),this.biker=this.add.image(config.width/2,380,"biker")}update(){if(this.biker.x+=6,this.biker.x>config.width-100){this.biker.flipX=!0,this.biker.x-=6;const e=this.add.text(config.width/2-150,450,"Click Here to Start the Game",{fill:"#fff"});e.setInteractive(),e.on("pointerdown",(()=>{this.scene.start("GameScreen")}))}}}